mapper.select:
select id,name,number,url,user_id,gmt_create,gmt_modify,user_id
from idcard
where id = #{id} and user_id = #{userId}

select id,name,number,url,user_id,gmt_create,gmt_modify,user_id
from idcard
where name = #{name} and user_id = #{userId}

mapper.update:
update idcard set gmt_modify = #{gmtModify}

    , name = #{name}


    , number = #{number}


    , url = #{url}

where user_id = #{userId} and id = #{id}
