mapper.insert:
    	insert into user(username,password

	    	,email


    		,gmt_create


    		,gmt_modify


    		,gmt_register

    	) values(#{username},#{password}

    		,#{email}


    		,#{gmtCreate}


    		,#{gmtModify}


    		,#{gmtRegister}



            SELECT LAST_INSERT_ID() AS id 


    	)

mapper.select:
    	select id,gmt_create gmtCreate,gmt_modify gmtModify,gmt_register gmtRegister,username,password,email,mobilephone,qq,weixin,weibo,identitycard,token
    	from user  
    	where 1=1

	      and id=#{id}


	      and gmt_create=#{gmtCreate}


	      and gmt_modify=#{gmtModify}


	      and gmt_register=#{gmtRegister}


	      and username=#{username}


	      and password=#{password}


	      and email=#{email}


	      and mobilephone=#{mobilephone}


	      and qq=#{qq}


	      and weixin=#{weixin}


	      and weibo=#{weibo}


	      and identitycard=#{identitycard}


	      and token=#{token}

mapper.update:
        update user set
	       gmt_create=#{gmtCreate}
	      ,gmt_modify=#{gmtModify}
	      ,gmt_register=#{gmtRegister}
	      ,username=#{username}
	      ,password=#{password}
	      ,email=#{email}
	      ,mobilephone=#{mobilephone}   
	      ,qq=#{qq}
	      ,weixin=#{weixin}
	      ,weibo=#{weibo}
	      ,identitycard=#{identitycard}
	      ,token=#{token}
        where id = #{id}
